document.getElementById("name").addEventListener("input",function(e){var t=e.target.value;e.target.value=t.replace(/[^a-zA-Z\s]/g,"")}),document.getElementById("email").addEventListener("input",function(e){var t=e.target.value;/^[^\s@]+@[^\s@]+\.[^\s@]+$/.test(t)?e.target.setCustomValidity(""):e.target.setCustomValidity("Por favor, insira um endereço de email válido.")}),document.querySelector("form").addEventListener("submit",function(e){document.getElementById("name").value?document.getElementById("email").value||(e.preventDefault(),alert("Por favor, preencha o campo Email.")):(e.preventDefault(),alert("Por favor, preencha o campo Nome."))});